import { resolvers } from "./resolvers";
import { makeExecutableSchema } from "graphql-tools";


const typeDefs = `

    type Query {
        hello: String
        consulta2 (parametro:String!): String
        tasks: [Task]
    }

    type Task {
        _id : ID,
        title : String!,
        description : String!,
        number : Int
    }

    type Mutation {
        createTask(input:inputTask) : Task
    }

    input inputTask {
        title : String!,
        description : String!,
        number : Int
    }

`;

export default makeExecutableSchema({
    typeDefs : typeDefs,
    resolvers : resolvers
})

